import { Component, OnInit, Input, Output, EventEmitter, ChangeDetectionStrategy } from '@angular/core';
import { Item } from 'src/app/Item.interface';

@Component({
  selector: 'app-filter-div',
  templateUrl: './filter-div.component.html',
  styleUrls: ['./filter-div.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class FilterDivComponent implements OnInit {
  @Input() tags: string[];
  @Output() filterSelected: EventEmitter<string> = new EventEmitter<string>();
  @Output() onSort: EventEmitter<string> = new EventEmitter<string>();

  sort_order = false; 
  filter_applied = false;
  constructor() { }

  sort_by_price(sortOrder:string){
    this.onSort.emit(sortOrder);
  }

  onfilterSelection(tagName:string){
    this.filter_applied = true;
    this.filterSelected.emit(tagName); 
  }

  ngOnInit(): void {
  }

}
<div >
    <span id="filter_tags_area">
        <b style="font-size: 1.1em"> FILTERS : </b>

        <span class="dim_text btn tags" (click)="onfilterSelection('all')" tabindex="0"> All Products </span>
        <span class="dim_text btn tags text-capitalize" *ngFor="let tag of tags" (click)="onfilterSelection(tag)" tabindex="1">
            {{tag}}
        </span>
    </span>

    <span id="sorting_span" mat-raised-button (click)="sort_order=!sort_order" style="float: right;" tabindex="2"> 
        <span class="dim_text">
            Sort By:  
        </span>
        <span *ngIf="sort_order" (click)="sort_by_price('asc')" tabindex="3">
            <b> Price Low to High </b>
            <span class="glyphicon glyphicon-chevron-down"></span>
        </span>
        <span *ngIf="!sort_order" (click)="sort_by_price('desc')" tabindex="4">
            <b> Price High to Low </b>
            <span class="glyphicon glyphicon-chevron-up"></span>
        </span>
    </span>

    <button *ngIf="filter_applied" mat-raised-button (click)="onfilterSelection('clear')" style="float: right;">
        Clear Filter
    </button>

</div>
